      // Update existing shift
      setShifts((prev) =>
        prev.map((s) =>
          s.id === editingShift.id
            ? {
                ...s,
                employeeId: formEmployeeId,
                startTime: formStartTime,
                endTime: formEndTime,
              }
            : s
        )
      );
    } else {
      // Add new shift
      const newShift: Shift = {
        id: Date.now().toString(),
        employeeId: formEmployeeId,
        date: selectedDate,
        startTime: formStartTime,
        endTime: formEndTime,
      };
      setShifts((prev) => [...prev, newShift]);
