                    </Button>
                  </div>

                  {/* Shifts */}
                  <div className="space-y-1">
                    {getShiftsForDay(day).map((shift) => {
                      const employee = getEmployeeById(shift.employeeId);
                      return (
                        <div
                          key={shift.id}
                          onClick={() => openEditDialog(shift)}
                          className="p-2 rounded-md cursor-pointer hover:opacity-80 transition-opacity"
                          style={{
                            backgroundColor: employee?.color + '20',
                            borderLeft: `3px solid ${employee?.color}`,
                          }}
                        >
                          <p
                            className="text-xs font-medium truncate"
                            style={{ color: employee?.color }}
                          >
                            {employee?.name}
                          </p>
                          <p className="text-xs text-gray-600">
                            {shift.startTime} - {shift.endTime}
                          </p>
                        </div>
                      );
                    })}
                    {getShiftsForDay(day).length === 0 && (
                      <p className="text-xs text-gray-400 text-center py-4">
                        Keine Schichten
                      </p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Employees Legend */}
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-lg">Mitarbeiter</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex flex-wrap gap-2">
              {employees.map((emp) => (
                <Badge
                  key={emp.id}
                  variant="outline"
                  className="px-3 py-1"
                  style={{
                    borderColor: emp.color,
                    backgroundColor: emp.color + '10',
                  }}
                >
                  <span
                    className="w-2 h-2 rounded-full mr-2"
                    style={{ backgroundColor: emp.color }}
                  />
                  {emp.name} - {emp.role}
                </Badge>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Edit/Add Dialog */}
      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>
              {editingShift ? 'Schicht bearbeiten' : 'Neue Schicht hinzuf√ºgen'}
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4 py-4">
            {selectedDate && (
              <p className="text-sm text-gray-500">
                Datum:{' '}
                {format(new Date(selectedDate), 'EEEE, d. MMMM yyyy', {
                  locale: de,
                })}
              </p>
            )}

            <div className="space-y-2">
              <Label htmlFor="employee">Mitarbeiter</Label>
              <Select value={formEmployeeId} onValueChange={setFormEmployeeId}>
                <SelectTrigger>
                  <SelectValue placeholder="Mitarbeiter ausw√§hlen" />
                </SelectTrigger>
                <SelectContent>
                  {employees.map((emp) => (
                    <SelectItem key={emp.id} value={emp.id}>
                      <div className="flex items-center gap-2">
                        <span
                          className="w-2 h-2 rounded-full"
                          style={{ backgroundColor: emp.color }}
                        />
                        {emp.name}
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="startTime">Startzeit</Label>
                <Input
                  id="startTime"
                  type="time"
                  value={formStartTime}
                  onChange={(e) => setFormStartTime(e.target.value)}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="endTime">Endzeit</Label>
                <Input
                  id="endTime"
                  type="time"
                  value={formEndTime}
                  onChange={(e) => setFormEndTime(e.target.value)}
                />
              </div>
            </div>
          </div>

          <DialogFooter className="flex gap-2">
            {editingShift && (
              <Button
                variant="destructive"
                onClick={() => {
                  handleDeleteShift(editingShift.id);
                  setIsDialogOpen(false);
                }}
              >
                <Trash2 className="h-4 w-4 mr-2" />
                L√∂schen
              </Button>
            )}
            <DialogClose asChild>
              <Button variant="outline">Abbrechen</Button>
            </DialogClose>
            <Button onClick={handleSaveShift} disabled={!formEmployeeId}>
              {editingShift ? 'Speichern' : 'Hinzuf√ºgen'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}
